import React from "react"
import BestSeller from "./BestSeller"
import { useState, useEffect } from "react"

export default function BestSellers(){

    let best_sellers = []

    let [itemData, setItemData] = useState(null)

    useEffect( () =>{
        fetch("/best-sellers")
        .then(response => response.json())
        .then(data => setItemData(data))
    }, []);

    console.log(itemData)

    if(itemData){
        for (let i = 0; i < itemData.length; i++){
            best_sellers.push(<BestSeller data={itemData[i]}/>)
        }
    }
    console.log(best_sellers)
    return(
        <div className="best-seller-list-container">
            <article className="best-seller-list">
                {itemData && itemData[response].map(obj =>{
                    <BestSeller data={obj} />
                })}
                {/* <BestSeller />
                <BestSeller />
